  // Remove 'delete file button' when application is submitted
  document.querySelector("i[ref=\"removeLink\"]").style.display = 'none';
} else {
// Remove link to file for public
  var links = document.querySelectorAll("a[ref='fileLink']")
  for(var i=0; i<links.length; i++) {
  	var fileName = links[i].innerText.split("\n").pop();
  	var linkParent = links[i].parentNode;
  	linkParent.removeChild(links[i]);
  	linkParent.innerHTML = fileName; 
  }
}

